  /**
   * 生命周期函数--监听页面加载
   */
  onLoad: function (options) {
    // 后端获取数据
    // this.getMsgInfo()
    
    // 云开发获取文章信息
    // wx.cloud.database().collection('articles')
    // .get()
    // .then(res => {
    //   console.log(res);
    //   this.setData({
    //     msgList: res.data.map((item) => { 
    //       return {...item.messages, _id: item._id}
    //     }).reverse()
    //   })
    // })

    this.getMoreMsg()
    this.setData({
      userInfo: wx.getStorageSync('userInfo')
    })
  },

  // 获取文章数据
  async getMsgInfo() {
    console.log('111');
    let msgListData = await request('/api/article')
    this.setData({
      msgList: msgListData.reverse()
    })
    console.log(this.data.msgList);
  },

  // 标题
  inputTitle(e) {
    console.log(e);
    this.setData({
      title: e.detail.value
    })
  },

  // 内容
  inputDetail(e) {
    this.setData({
      msgdetail: e.detail.value
    })
  },

  // 提交文章
  addMsg(e) {
    //判断用户是否登录
    let userInfo = wx.getStorageSync('userInfo')
    if(!userInfo){
      wx.showToast({
        title: '请先登录',
        icon: 'none',
        success: () => { 
          // 跳转到个人中心页面
          wx.reLaunch({
            url: '/pages/personal/personal'
          })
        }
      })
    }
    this.setData({
      modalName: e.currentTarget.dataset.target
    })
  },

  // 发布文章的回调
  publishMsg() {
    let userInfo = wx.getStorageSync('userInfo')
    console.log(userInfo)
    let msgObj = {
      id: this.data.msgList.length + 1,
      title: this.data.title,
      userPic: userInfo.avatarUrl,
      nickName: userInfo.nickName,
      msg: this.data.msgdetail,
      img: '',
      appreciate: 0,
      discuss: 0,
      collect: 0,
      transmit: 0,
    }
    console.log(msgObj);
    // 发送文章给后端
    // request('/api/article', msgObj, 'POST')

    wx.cloud.database().collection('articles')
    .add({
      data:{
        messages:msgObj
      }
    })
    .then(res => {
      console.log(res);
      // this.setData({
      //   list: res.data[0].eat
      // })
      // wx.hideLoading()
    })

    this.setData({
      title: '',
      msgdetail: '',
      modalName: null,
      msgList: [msgObj, ...this.data.msgList]
    })
  },

  // 显示弹框
  showModal(e) {
    this.setData({
      modalName: e.currentTarget.dataset.target
    })
  },

  // 隐藏弹框
  hideModal(e) {
    console.log('hide');
    this.setData({
      modalName: null
    })
  },

  handleSearch() { //跳转至搜索页面
    wx.navigateTo({
      url: '/pages/search/search',
    })
  },

  // 点击阅读文章的回调
  handleToMsg(e) {
    let index = e.currentTarget.id
    console.log(e.currentTarget.id);
    let msg = this.data.msgList[index]
    console.log('msg',msg);
    // msg = encodeURIComponent(JSON.stringify(msg)) //进行编码
    const _id = msg._id
    wx.navigateTo({
      url: '/pages/message/message?id=' + _id
    })
  }, 

  handlePic() { //点击选择图片的回调
    wx.chooseImage({
      count: 1,
      sizeType: ['original', 'compressed'],
      sourceType: ['album', 'camera'],
      success (res) {
        // tempFilePath可以作为img标签的src属性显示图片
        const tempFilePaths = res.tempFilePaths
        console.log(tempFilePaths);
        this.setData({
          msgdetail: this.data.msgdetail + tempFilePaths
        })
      }
    })
  },
  
  // 选择视频
  handleVideo() {
    wx.chooseVideo({
      sourceType: ['album','camera'],
      maxDuration: 60,
      camera: 'back',
      compressed: false,
      success(res) {
        console.log(res)
      }
    })
  },    

  ChooseImage() {
    wx.chooseImage({
      count: 4, //默认9
      sizeType: ['original', 'compressed'], //可以指定是原图还是压缩图，默认二者都有
      sourceType: ['album'], //从相册选择
      success: (res) => {
        if (this.data.imgList.length != 0) {
          this.setData({
            imgList: this.data.imgList.concat(res.tempFilePaths)
          })
        }else {
          this.setData({
            imgList: res.tempFilePaths
          })
        }
      }
    });
  },
  ViewImage(e) {
    wx.previewImage({
      urls: this.data.imgList,
      current: e.currentTarget.dataset.url
    });
  },
  DelImg(e) {
    wx.showModal({
      content: '确定要删除吗？',
      cancelText: '点错了',
      confirmText: '确定',
      success: res => {
        if (res.confirm) {
          this.data.imgList.splice(e.currentTarget.dataset.index, 1);
          this.setData({
            imgList: this.data.imgList
          })
        }
      }
    }) 
  },

  getMoreMsg() {
    wx.showLoading({
      title: '加载中...',
    })
    wx.cloud.database().collection('articles')
      .orderBy('time', 'desc')
      .skip(this.data.msgList.length)
      .get()
      .then(res => {
        wx.hideLoading()
        console.log(res.data);
        if (res.data.length == 0) {
          wx.showToast({
            icon: 'none',
            title: '没有更多数据了~',
          })
        } else {
          this.setData({
            msgList: this.data.msgList.concat(res.data.map((item, index) => {
              return {...item.messages, _id: item._id, id: index+this.data.msgList.length}
            }))
          })
          console.log(this.data.msgList);
        }
      })
    // const msgList = this.data.msgList.map((item, index) => {
    //   return {...item, id: index}
    // })
    // console.log(msgList)
    // this.setData({
    //   msgList
    // })

    
  },


